function dailyChart() {
  var dataSource = $("#chart").attr("data-source");
  if (dataSource != undefined) {
    var data = JSON.parse(dataSource);
    var chart = c3.generate({
      bindto: "#chart",
      data: {
        columns: data,
        type : 'spline'
      },
    });
  }
}


